package qfpay.wxshop.activity;/** * 货源引导界面 * */import org.androidannotations.annotations.Click;import org.androidannotations.annotations.EActivity;import org.androidannotations.annotations.ViewById;import qfpay.wxshop.R;import qfpay.wxshop.WxShopApplication;import qfpay.wxshop.app.BaseActivity;import qfpay.wxshop.ui.main.MainTab;import qfpay.wxshop.ui.main.fragment.HuoYuanFragment;import android.os.Bundle;import android.widget.Button;import android.widget.ImageView;@EActivity(R.layout.main_official_is_what)public class OfficialGoodIsWhatActivity extends BaseActivity {		@ViewById	ImageView imageView1;	@ViewById	Button btn_go;	@Click	void btn_go() {		WxShopApplication.dataEngine.isSetFirstinOfficalActivity(true);		HuoYuanFragment fragment = (HuoYuanFragment) MainTab.HUOYUAN                .getFragment();		if(fragment!=null){			fragment.changePager(0);		}		finish();	}	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		overridePendingTransition(R.anim.push_up_in, R.anim.push_down_in);	}}