package qfpay.wxshop.ui.view;public class Tag{	private String text;	private boolean display;	private boolean checked;	public Tag(String text,boolean display){		this.setText(text);		this.setDisplay(display);	}	public boolean isDisplay() {		return display;	}	public void setDisplay(boolean display) {		this.display = display;	}	public String getText() {		return text;	}	public void setText(String text) {		this.text = text;	}	public boolean isChecked() {		return checked;	}	public void setChecked(boolean checked) {		this.checked = checked;	}}